import React, { useState } from 'react';
import axios from 'axios';
import './UniTable.css';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';

const UniTable = () => {
  const [universities, setUniversities] = useState([]);

  const loadUniversities = async () => {
    try {
      const response = await axios.get('http://universities.hipolabs.com/search?country=Australia');
      setUniversities(response.data);
    } catch (error) {
      console.error('Error fetching the university data', error);
    }
  };

  const deleteLastUniversity = () => {
    setUniversities(universities.slice(0, -1));
  };

  const addFirstUniversityToEnd = () => {
    if (universities.length > 0) {
      setUniversities([...universities, universities[0]]);
    }
  };

  return (
    <div className="uni-table-container">
    <div className='buttons'>
      <Stack direction="row" spacing={2} justifyContent="center">
        <Button variant="contained" onClick={loadUniversities}>LOAD</Button>
        <Button variant="contained" color="error" onClick={deleteLastUniversity}>DELETE</Button>
        <Button  variant="contained" color="success" onClick={addFirstUniversityToEnd}>ADD</Button>
        </Stack>
    </div>
    <TableContainer component={Paper} sx={{ maxHeight: 800 }}>
        <Table stickyHeader>
          <TableHead>
            <TableRow>
            <TableCell sx={{ backgroundColor: '#f4f4f4' }}>Name</TableCell>
            <TableCell sx={{ backgroundColor: '#f4f4f4' }}>State</TableCell>
            <TableCell sx={{ backgroundColor: '#f4f4f4' }}>Country</TableCell>
            <TableCell sx={{ backgroundColor: '#f4f4f4' }}>Website</TableCell>
            </TableRow>
          </TableHead>
        <TableBody>
          {universities.map((university, index) => (
            <TableRow key={index}>
              <TableCell>{university.name}</TableCell>
              <TableCell>{university['state-province'] || 'N/A'}</TableCell>
              <TableCell>{university.country}</TableCell>
              <TableCell><a href={university.web_pages[0]} target="_blank" rel="noopener noreferrer">{university.web_pages[0]}</a></TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </div>
  );
};

export default UniTable;
